import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'

function Box(props) {
  return (
    <div class="container" style={{ backgroundColor: props.myColor, border: '2px solid ' }}></div>
  )
}

ReactDOM.render(
  <>
    <Box myColor="red"  border/>
    <Box myColor="yellow" />
    <Box myColor="cyan" />
  </>, document.getElementById("root")
)
